mapper.select:
select * from ielts_teacher_operate
where teacher_id = #{t.teacher_id,jdbcType=INTEGER}
and create_time > #{t.left_integral_time,jdbcType=TIMESTAMP}
and create_time < #{t.right_integral_time,jdbcType=TIMESTAMP}

select * from ielts_teacher_operate
where teacher_id = #{teacher_id,jdbcType=INTEGER}
and create_time > #{left_integral_time,jdbcType=TIMESTAMP}
and create_time < #{right_integral_time,jdbcType=TIMESTAMP}

mapper.insert:
insert into ielts_teacher_operate (teacher_id, operate_num, create_time
  )
values (#{teacher_id,jdbcType=INTEGER}, #{operate_num,jdbcType=INTEGER}, #{create_time,jdbcType=TIMESTAMP}
  )

mapper.update:
update ielts_teacher_operate
set operate_num = #{operate_num,jdbcType=INTEGER},
  create_time = #{create_time,jdbcType=TIMESTAMP}
where id = #{teacher_id,jdbcType=INTEGER}
