SELECT r.id,r.resume_seeker_id,r.hr_company_id,r.flow_status,m.company_name AS companyName,o.NAME AS deptName, h.NAME AS positionName ,r.apply_position,r.name ,r.gra_school,r.high_edu,r.major, (CASE f.current_node WHEN 'endevent' THEN '已归档' WHEN 'rlqrTask' THEN '人力确认' ELSE p.custom_node_name END ) AS resumeStatus, r.source,u.name AS inside_recommend,r.telephone,r.delivery_date ,r.email, (CASE f.current_node WHEN 'endevent' THEN f.process_end_time WHEN 'rlqrTask' THEN f.lose_time ELSE a.approve_time END )AS approveTime, f.flow_code AS flowCode,f.status,f.id flow_id FROM resume_base r LEFT JOIN hr_company m ON r.hr_company_id=m.company_id LEFT JOIN hr_position h ON r.matching_position=h.ID LEFT JOIN hr_company c ON h.COMID=c.company_id LEFT JOIN hr_organization o ON h.DEPT=o.ID LEFT JOIN hr_resume_flow f ON r.id=f.resume_id LEFT JOIN position_process_node_config p ON f.current_node=p.node_id and f.position_process_id=p.position_process_id LEFT JOIN (SELECT flow_code,MAX(id) id FROM act_custom_comment GROUP BY flow_code )a1 ON a1.flow_code = f.flow_code LEFT JOIN act_custom_comment a ON a.id = a1.id AND a.flow_code = a1.flow_code LEFT JOIN postion_watcher w on r.matching_position=w.position_id LEFT JOIN hr_user u ON r.inside_recommend=u.email_addr where r.original_flag IN('0','2') AND r.save_flag='0' AND w.watcher_id=#{userName,jdbcType=VARCHAR}
AND r.hr_company_id in
#{companyId,jdbcType=VARCHAR}
AND r.hr_company_id=#{t.hrCompanyId,jdbcType=VARCHAR}
AND o.ID=#{t.deptId,jdbcType=VARCHAR}
AND h.ID=#{t.positionId,jdbcType=VARCHAR}
AND r.source=#{t.source,jdbcType=VARCHAR}
AND r.inside_recommend is not null
AND p.custom_node_name like CONCAT(CONCAT('%', #{t.resumeStatus,jdbcType=VARCHAR}), '%')
AND r.name like CONCAT(CONCAT('%', #{t.name,jdbcType=VARCHAR}), '%')
AND r.telephone like CONCAT(CONCAT('%', #{t.telephone,jdbcType=VARCHAR}), '%')
AND r.gra_school like CONCAT(CONCAT('%', #{t.graSchool,jdbcType=VARCHAR}), '%')
AND r.high_edu like CONCAT(CONCAT('%', #{t.highEdu,jdbcType=VARCHAR}), '%')
AND r.apply_position like CONCAT(CONCAT('%', #{t.applyPosition,jdbcType=VARCHAR}), '%')
AND r.delivery_date >= #{t.deliveryDateStart,jdbcType=TIMESTAMP}
AND r.delivery_date <= #{t.deliveryDateEnd,jdbcType=TIMESTAMP}
order By r.delivery_date DESC

